package com.chainsys.day2;

public class AssignmentOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=10;
		System.out.println(x);
		x+=5;
		System.out.println(x);
		x-=5;
		System.out.println(x);
		x*=5;
		System.out.println(x);
		x/=5;
		System.out.println(x);
		x%=5;
	}

}
