package com.chainsys.day3;

public class Equation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int y = 5; y <= 10; y++) {
            double result = 2 * y * y + y + 5;
            System.out.println("For y = " + y + ", the result is: " + result);
        }
    }
}
