package com.chainsys.day3;

public class RelationalOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=3;
		int y=5;
		
		System.out.println("x<y is "+(x<y));
		System.out.println("x<=y is "+(x<=y));
		System.out.println("x>y is "+(x>y));
		System.out.println("x>=y is "+(x>=y));
		System.out.println("x>=y is "+(x==y));
		
		char a='x';
		char b='y';
		
		System.out.println("a<b is "+(a<b));
		System.out.println("a<=b is "+(a<=b));
		System.out.println("a<=b is "+(a>b));
		System.out.println("a>=b is "+(a>=b));
		System.out.println("a==b is "+(a==b));
		
	}

}
